package com.ywhk.ckb.dao.model.core;import lombok.Data;import org.hibernate.annotations.GenericGenerator;import javax.persistence.*;import java.util.Date;/** * 中医病症 */@Data@Entity@Table(name="KLN_BAS_ChinaMalad")public class ChinaMaladEntity {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "ChinaMaladID", nullable = false)    private Integer chinaMaladID;    @Column(name = "FDiseaseID", nullable = false)    private Integer FDiseaseID;    @Column(name = "FOrder", nullable = false)    private Integer FOrder;    @Column(name = "FNumber", nullable = false)    private Integer FNumber;    @Column(name = "FName", nullable = false, length = 1024)    private String FName;    @Column(name = "FSymptom", nullable = false, length = 16)    private String FSymptom;    @Column(name = "FPathogenesis", nullable = false, length = 1024)    private String FPathogenesis;    @Column(name = "FTherapy", nullable = false, length = 16)    private String FTherapy;    @Column(name = "Fpy", nullable = false, length = 1024)    private String Fpy;    @Column(name = "Fwb", nullable = false, length = 256)    private String Fwb;    @Column(name = "Fexp", nullable = false, length = 256)    private String Fexp;    @Column(name = "Fmark", nullable = false)    private Integer Fmark;    @Column(name = "FCreateDate", nullable = false, length = 8)    private Date FCreateDate;    @Column(name = "FCreateuserID", nullable = false)    private Integer FCreateuserID;    @Column(name = "FModifyDate", nullable = false, length = 8)    private Date FModifyDate;    @Column(name = "FModifyUserID", nullable = false)    private Integer FModifyUserID;}