package com.ywhk.ckb.dao.model.core;import lombok.Data;import org.hibernate.annotations.GenericGenerator;import javax.persistence.*;import java.util.Date;/** * 科室 */@Data@Entity@Table(name="SYS_Group")public class GroupEntity {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "FGROUPID", nullable = false)    private Integer FGROUPID;    @Column(name = "MechanismID", nullable = false)    private Integer MechanismID;    @Column(name = "FOrderNumber", nullable = false)    private Integer FOrderNumber;    @Column(name = "FNumber", nullable = false, length = 50)    private String FNumber;    @Column(name = "FName", nullable = false, length = 50)    private String FName;    @Column(name = "FPy", nullable = false, length = 50)    private String FPy;    @Column(name = "FWb", nullable = false, length = 50)    private String FWb;    @Column(name = "FExp", nullable = false, length = 200)    private String FExp;    @Column(name = "Fmark", nullable = false)    private Integer Fmark;    @Column(name = "FCreateDate", nullable = false, length = 8)    private Date FCreateDate;    @Column(name = "FCreateuserID", nullable = false)    private Integer FCreateuserID;    @Column(name = "FModifyDate", nullable = false, length = 8)    private Date FModifyDate;    @Column(name = "FModifyUserID", nullable = false)    private Integer FModifyUserID;}