package com.ywhk.ckb.dao.model.core;import lombok.Data;import org.hibernate.annotations.GenericGenerator;import javax.persistence.*;import java.util.Date;@Data@Entity@Table(name="KLN_BAS_ItemAdviceDetail")public class ItemAdviceDetailEntity {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "ItemAdviceDetailID", nullable = false)    private Integer itemAdviceDetailID;    @Column(name = "FOrder", nullable = false)    private Integer FOrder;    @Column(name = "FNumber", nullable = false, length = 10)    private String FNumber;    @Column(name = "FName", nullable = false, length = 256)    private String FName;    @Column(name = "FItemAdviceName", nullable = false, length = 16)    private String FItemAdviceName;    @Column(name = "FDiseaseID", nullable = false)    private Integer FDiseaseID;    @Column(name = "FChinaMaladID", nullable = false)    private Integer FChinaMaladID;    @Column(name = "FModification", nullable = false, length = 2560)    private String FModification;    @Column(name = "Fpy", nullable = false, length = 128)    private String FPy;    @Column(name = "Fwb", nullable = false, length = 128)    private String FWb;    @Column(name = "Fexp", nullable = false, length = 128)    private String FExp;    @Column(name = "Fmark", nullable = false)    private Integer FMark;    @Column(name = "FCreateDate", nullable = false, length = 8)    private Date FCreateDate;    @Column(name = "FCreateuserID", nullable = false)    private Integer FCreateuserID;    @Column(name = "FModifyDate", nullable = false, length = 8)    private Date FModifyDate;    @Column(name = "FModifyUserID", nullable = false)    private Integer FModifyUserID;}