package com.ywhk.ckb.dao.model.core;import lombok.Data;import javax.persistence.*;import java.util.Date;/** * 药品目录 */@Data@Entity@Table(name = "KLN_BAS_MedSpec")public class MedSpecEntity {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "FMedSpecID", nullable = false)    private Integer FMedSpecID;    @Column(name = "DirectoryID", nullable = false)    private Integer directoryID;    @Column(name = "FOrder", nullable = false)    private Integer FOrder;    @Column(name = "FNumber", nullable = false, length = 50)    private String FNumber;    @Column(name = "FMedSpecName", nullable = false, length = 501)    private String FMedSpecName;    @Column(name = "FAlias", nullable = false, length = 50)    private String FAlias;    @Column(name = "FSpec", nullable = false, length = 200)    private String FSpec;    @Column(name = "FUnit", nullable = false, length = 50)    private String FUnit;    @Column(name = "FMedDosage", nullable = false, length = 50)    private String FMedDosage;    @Column(name = "FShape", nullable = false, length = 16)    private String FShape;    @Column(name = "FCharacter", nullable = false, length = 16)    private String FCharacter;    @Column(name = "FComposition", nullable = false, length = 16)    private String FComposition;    @Column(name = "FPharmacologicalEffects", nullable = false, length = 16)    private String FPharmacologicalEffects;    @Column(name = "FProcessingDrugs", nullable = false, length = 16)    private String FProcessingDrugs;    @Column(name = "FFlavor", nullable = false, length = 1000)    private String FFlavor;    @Column(name = "FChannelTropism", nullable = false, length = 1000)    private String FChannelTropism;    @Column(name = "FFunction", nullable = false, length = 1000)    private String FFunction;    @Column(name = "Findications", nullable = false, length = 16)    private String Findications;    @Column(name = "FClassTypeID", nullable = false, length = 1000)    private String FClassTypeID;    @Column(name = "FNote", nullable = false, length = 1000)    private String FNote;    @Column(name = "FClinicalPractice", nullable = false, length = 16)    private String FClinicalPractice;    @Column(name = "FDosage", nullable = false, length = 1000)    private String FDosage;    @Column(name = "FPrescribingExamples", nullable = false, length = 16)    private String FPrescribingExamples;    @Column(name = "FPy", nullable = false, length = 1000)    private String FPy;    @Column(name = "FWb", nullable = false, length = 50)    private String FWb;    @Column(name = "FExp", nullable = false, length = 2000)    private String FExp;    @Column(name = "FMark", nullable = false)    private Integer FMark;    @Column(name = "FCreateDate", nullable = false, length = 8)    private Date FCreateDate;    @Column(name = "FCreateuserID", nullable = false)    private Integer FCreateuserID;    @Column(name = "FModifyDate", nullable = false, length = 8)    private Date FModifyDate;    @Column(name = "FModifyUserID", nullable = false)    private Integer FModifyUserID;}