package com.ywhk.ckb.dao.model.core;import lombok.Data;import org.hibernate.annotations.GenericGenerator;import javax.persistence.*;import java.util.Date;/** * 药品说明书 */@Data@Entity@Table(name = "KLN_BAS_MedSpecInstruction")public class MedSpecInstructionEntity {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "FMedSpecInstruction", nullable = false)    private Integer FMedSpecInstruction;    @Column(name = "FMedSpecID", nullable = false)    private Integer FMedSpecID;    @Column(name = "FOrder", nullable = false)    private Integer FOrder;    @Column(name = "Fmanufacturer", nullable = false, length = 510)    private String Fmanufacturer;    @Column(name = "FRugInstruction", nullable = false, length = 16)    private String FRugInstruction;    @Column(name = "FExp", nullable = false, length = 16)    private String FExp;    @Column(name = "FMark", nullable = false)    private Integer FMark;    @Column(name = "FPharmacologicalEffects", nullable = false, length = 16)    private String FPharmacologicalEffects;    @Column(name = "FCreateDate", nullable = false, length = 8)    private Date FCreateDate;    @Column(name = "FCreateuserID", nullable = false)    private Integer FCreateuserID;    @Column(name = "FModifyDate", nullable = false, length = 8)    private Date FModifyDate;    @Column(name = "FModifyUserID", nullable = false)    private Integer FModifyUserID;}