package com.ywhk.ckb.dao.model.core;import lombok.Data;import javax.persistence.*;/** * 方剂明细表 */@Data@Entity@Table(name = "T_Kld_ItemAdviceDetail")public class TItemAdviceDetailEntity {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "FAdviceetailID", nullable = false)    private Integer FItemAdviceID;    @Column(name = "FItemAdviceID", nullable = false)    private Integer FDiseaseID;    @Column(name = "FTypeID", nullable = false)    private Integer FDiseaseNumber;    @Column(name = "FMedSpecID", nullable = false)    private Integer FDiseaseNAME;    @Column(name = "FItemID", nullable = false)    private Integer FNumber;    @Column(name = "FFREQUENCYTYPEID", nullable = false)    private Integer FNAME;    @Column(name = "FDose", nullable = false, length = 10)    private String FContent;}