package com.ywhk.ckb.dao.model.core;import lombok.Data;import javax.persistence.*;import java.util.Date;/** * 方剂表 */@Data@Entity@Table(name = "T_Kld_ItemAdvice")public class TItemAdviceEntity {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "FItemAdviceID", nullable = false)    private Integer FItemAdviceID;    @Column(name = "FDiseaseID", nullable = false)    private Integer FDiseaseID;    @Column(name = "FDiseaseNumber", nullable = false, length = 16)    private String FDiseaseNumber;    @Column(name = "FDiseaseNAME", nullable = false, length = 256)    private String FDiseaseNAME;    @Column(name = "FNumber", nullable = false, length = 16)    private String FNumber;    @Column(name = "FNAME", nullable = false, length = 256)    private String FNAME;    @Column(name = "FContent", nullable = false, length = 1024)    private String FContent;    @Column(name = "FExp", nullable = false, length = 256)    private String FExp;}