package com.ywhk.ckb.dao.repository;import com.ywhk.ckb.dao.model.core.DiseaseListEntity;import com.ywhk.ckb.dao.model.core.DiseaseTypeEntity;import org.springframework.data.domain.Page;import org.springframework.data.domain.Pageable;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.JpaSpecificationExecutor;import java.util.List;public interface DiseaseListRepository extends JpaRepository<DiseaseListEntity, Integer>, JpaSpecificationExecutor<DiseaseListEntity>{    Page<DiseaseListEntity> findByFDiseaseTypeID(Integer id, Pageable pageable);    Page<DiseaseListEntity> findByFDiseaseNameLike(String name, Pageable pageable);    DiseaseListEntity findByFDiseaseID(Integer id);    DiseaseListEntity findByFDiseaseTypeID(Integer id);    Page<DiseaseListEntity> findByFDiseaseTypeIDAndFDiseaseNameLike(Integer id, String name, Pageable pageable);}