package com.ywhk.ckb.service;import com.ywhk.ckb.common.doc.annotation.ApiDocInterface;import com.ywhk.ckb.common.doc.annotation.ApiDocService;import com.ywhk.ckb.common.http.response.PaginationResponse;import com.ywhk.ckb.dao.model.core.MedSpecEntity;import com.ywhk.ckb.dao.model.core.MedSpecInstructionEntity;import com.ywhk.ckb.service.dto.medspec.*;import org.springframework.stereotype.Service;import java.util.List;@Service@ApiDocService("药品服务")public interface MedicineService {    @ApiDocInterface("查询药品分类")    List<DirectoryDto> queryMedType();    @ApiDocInterface("查询药品列表")    PaginationResponse<QueryAllMedListResponse> queryAllMedList(QueryAllMedListRequest request);    @ApiDocInterface("更新修改药品信息")    UpdateMedResponse updateMed(UpdateMedRequest request);    @ApiDocInterface("查询药品详情")    List<MedSpecEntity> queryMedDetail(MedSpecEntity medSpecEntity);    @ApiDocInterface("根据药品分类查询药品列表")    PaginationResponse<QueryMedListResponse> queryMedList(QueryMedListRequest request);    @ApiDocInterface("根据药品ID查询药品说明书")    List<MedSpecInstructionEntity> queryMedInstructionById(MedSpecInstructionEntity medSpecInstructionEntity);    @ApiDocInterface("删除药品")    void deleteInBatch(List<MedSpecEntity> list);}