package com.ywhk.ckb.service.impl;import com.ywhk.ckb.dao.model.core.ChinaMaladEntity;import com.ywhk.ckb.dao.repository.ChinaMaladRepository;import com.ywhk.ckb.service.ChinaMaladService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Service("chinaMaladService")public class ChinaMaladServiceImpl implements ChinaMaladService {    @Autowired    private ChinaMaladRepository chinaMaladRepository;    /**     * 中医病症查询     *     * @return     */    @Override    public List<ChinaMaladEntity> queryChinaMaladList() {        return chinaMaladRepository.findAll();    }    /**     * 中医病症删除     *     * @return     */    @Override    public void deleteChinaMaladById(Integer id) {        chinaMaladRepository.deleteById(id);    }    /**     * 中医病症     *     * @return     */    @Override    public ChinaMaladEntity updateChinaMaladEntity(ChinaMaladEntity chinaMaladEntity) {        Integer chinaMaladEntityId = chinaMaladEntity.getChinaMaladID();        chinaMaladRepository.save(chinaMaladEntity);        return chinaMaladRepository.findByChinaMaladID(chinaMaladEntityId);    }    /**     * 批量删除病症     *     * @return     */    @Override    public void deleteInBatch(List<ChinaMaladEntity> list) {        chinaMaladRepository.deleteInBatch(list);    }}