package com.ywhk.ckb.service.impl;import com.ywhk.ckb.dao.model.core.DifferentialEntity;import com.ywhk.ckb.dao.repository.DifferentialRepository;import com.ywhk.ckb.service.DifferentialService;import lombok.extern.slf4j.Slf4j;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Service("differentialService")public class DifferentialServiceImpl implements DifferentialService {    @Autowired    private DifferentialRepository differentialRepository;    /**     * 鉴别诊断列表查询     * @return     */    @Override    public List<DifferentialEntity> queryDifferentialList () {        return differentialRepository.findAll();    }    /**     * 鉴别诊断列表     * @return     */    @Override    public DifferentialEntity updateFDifferentialID(DifferentialEntity differentialEntity) {        Integer differentialEntityId = differentialEntity.getFDifferentialID();        DifferentialEntity oldMedSpecInstructionEntity = differentialRepository.findByFDifferentialID(differentialEntityId);        oldMedSpecInstructionEntity.setFDifferentialID(differentialEntity.getFDifferentialID());        differentialRepository.save(oldMedSpecInstructionEntity);        return differentialRepository.findByFDifferentialID(differentialEntityId);    }    /**     * 鉴别诊断列表删除     * @return     */    @Override    public void deletDifferentialById(Integer id) {        differentialRepository.deleteById(id);    }}