package com.ywhk.ckb.service.impl;import com.ywhk.ckb.dao.model.core.GroupEntity;import com.ywhk.ckb.dao.repository.GroupRepostory;import com.ywhk.ckb.service.GroupService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Service("groupService")public class GroupServiceImpl implements GroupService {    @Autowired    private GroupRepostory groupRepostory;    /**     * 查询科室列表信息     * @return     */    @Override    public List<GroupEntity> queryGroupList () {        return groupRepostory.findAll();    }    /**     * 科室列表     * @return     */    @Override    public GroupEntity updateGruopEntityID(GroupEntity gruopEntity) {        Integer gruopEntityId = gruopEntity.getFGROUPID();        GroupEntity oldDiseaseListEntity = groupRepostory.findByFGROUPID(gruopEntityId);        oldDiseaseListEntity.setFGROUPID(gruopEntity.getFGROUPID());        groupRepostory.save(oldDiseaseListEntity);        return groupRepostory.findByFGROUPID(gruopEntityId);    }    /**     * 科室列表删除     * @return     */    @Override    public void deletGruopEntityById(Integer id) {        groupRepostory.deleteById(id);    }    /**     * 批量删除科室     * @return     */    @Override    public void deleteInBatch(List<GroupEntity> list) {        groupRepostory.deleteInBatch(list);    }}