package com.ywhk.ckb.service.impl;import com.ywhk.ckb.dao.model.core.ItemAdviceDetailEntity;import com.ywhk.ckb.dao.repository.ItemAdviceDetailRepository;import com.ywhk.ckb.service.ItemAdviceDetailService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Service("itemAdviceSetail")public class QueryItemAdviceDetailListImpl implements ItemAdviceDetailService {    @Autowired    private ItemAdviceDetailRepository itemAdviceDetailRepository;    /**     * 中医方剂查询     * @return     */    @Override    public List<ItemAdviceDetailEntity> queryItemAdviceDetailList () {        return itemAdviceDetailRepository.findAll();    }    /**     * 中医方剂     * @return     */    @Override    public ItemAdviceDetailEntity updateItemAdviceDetailEntityID(ItemAdviceDetailEntity itemAdviceDetailEntity) {        Integer itemAdviceDetailEntityId = itemAdviceDetailEntity.getItemAdviceDetailID();        ItemAdviceDetailEntity oldItemAdviceDetailEntity = itemAdviceDetailRepository.findByItemAdviceDetailID(itemAdviceDetailEntityId);        oldItemAdviceDetailEntity.setItemAdviceDetailID(itemAdviceDetailEntity.getItemAdviceDetailID());        itemAdviceDetailRepository.save(oldItemAdviceDetailEntity);        return itemAdviceDetailRepository.findByItemAdviceDetailID(itemAdviceDetailEntityId);    }    /**     * 中医方剂删除     * @return     */    @Override    public void deletItemAdviceDetailEntityById(Integer id) {        itemAdviceDetailRepository.deleteById(id);    }    /**     * 批量删除中医方剂     * @return     */    @Override    public void deleteInBatch(List<ItemAdviceDetailEntity> list) {        itemAdviceDetailRepository.deleteInBatch(list);    }}